function startCountdown(e,t){var n,s=document.getElementById(e+"-seconds"),o=(new Date).getTime()+t*1e3,i=(new Date).getTime(),a=o-i,r=Math.ceil(a/1e3);s.innerHTML=r,n=setInterval(function(){var i=(new Date).getTime(),e=o-i,t=Math.ceil(e/1e3);if(s.innerHTML=t,e<0){clearInterval(n);return}t<=0&&clearInterval(n)},1e3)}function initializeCountdown(e,t){var n=document.getElementById(e);n&&n.addEventListener("beginEvent",function(){var e=parseFloat(n.getAttribute("dur"));if(isNaN(e)||e<=0){console.error("Invalid duration");return}startCountdown(t,e)})}